CREATE DATABASE web_xem_phim;
USE web_xem_phim;
CREATE TABLE users(
	user_name VARCHAR(50) PRIMARY KEY,
	user_password VARCHAR(100) NOT NULL,
    user_role enum('admin', 'user') NOT NULL
);
CREATE TABLE genres (
    genre_id INT PRIMARY KEY AUTO_INCREMENT,
    genre_name VARCHAR(30) NOT NULL UNIQUE
);
CREATE TABLE countries (
    country_id INT PRIMARY KEY AUTO_INCREMENT,
    country_name VARCHAR(20) NOT NULL UNIQUE
);
CREATE TABLE directors(
    director_id INT PRIMARY KEY AUTO_INCREMENT,
    director_name VARCHAR(50) NOT NULL
);
CREATE TABLE movies (
    movie_id INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(100) NOT NULL,
    content TEXT,
    release_day DATE,
    image_link TEXT, 
    directory TEXT,
    movie_status BIT,
    director_id INT,
    country_id INT,
    genre_id INT,
    CONSTRAINT fk_movies_directors FOREIGN KEY (director_id) REFERENCES directors(director_id) ON DELETE CASCADE,
    CONSTRAINT fk_movies_genres FOREIGN KEY (genre_id) REFERENCES genres(genre_id) ON DELETE CASCADE,
    CONSTRAINT fk_movies_countries FOREIGN KEY (country_id) REFERENCES countries(country_id) ON DELETE CASCADE
);
CREATE TABLE actors (
    actor_id INT PRIMARY KEY AUTO_INCREMENT,
    actor_name VARCHAR(50) NOT NULL
);
CREATE TABLE movie_actors (
	movie_actors_id INT PRIMARY KEY AUTO_INCREMENT,
    movie_id INT,
    actor_id INT,
    CONSTRAINT fk_movie_actors_movies FOREIGN KEY (movie_id) REFERENCES movies(movie_id) ON DELETE CASCADE,
    CONSTRAINT fk_movie_actors_actors FOREIGN KEY (actor_id) REFERENCES actors(actor_id) ON DELETE CASCADE
);
CREATE TABLE user_ratings (
    user_ratings_id INT PRIMARY KEY AUTO_INCREMENT,
    rating INT NOT NULL,
    user_name VARCHAR(50),
    movie_id INT,
    CONSTRAINT fk_user_ratings_users FOREIGN KEY (user_name) REFERENCES users(user_name) ON DELETE CASCADE,
    CONSTRAINT fk_user_ratings_movies FOREIGN KEY (movie_id) REFERENCES movies(movie_id) ON DELETE CASCADE

);
CREATE TABLE favorite_movies (
	favorite_movies_id INT PRIMARY KEY AUTO_INCREMENT,
    user_name VARCHAR(50),
    movie_id INT,
    CONSTRAINT fk_favorite_movies_users FOREIGN KEY (user_name) REFERENCES users(user_name) ON DELETE CASCADE,
    CONSTRAINT fk_favorite_movies_movies FOREIGN KEY (movie_id) REFERENCES movies(movie_id) ON DELETE CASCADE
);
CREATE TABLE movie_views (
	movie_views_id INT PRIMARY KEY AUTO_INCREMENT,
    user_name VARCHAR(50),
    movie_id INT,
    view_time TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    CONSTRAINT fk_movieviews_users FOREIGN KEY (user_name) REFERENCES users(user_name) ON DELETE CASCADE,
    CONSTRAINT fk_movieviews_movies FOREIGN KEY (movie_id) REFERENCES movies(movie_id) ON DELETE CASCADE
);

