INSERT INTO movies (title, content, release_day, image_link, directory, movie_status, director_id, country_id, genre_id)
VALUES
    ('PT Tại Gia - Lái Mẹ Chăn Con Gái', 'Nữ đại gia thành đạt tìm PT tại gia với mong muốn lấy lại sức khỏe vóc dáng, nhưng ngoài phục vụ cuộc sống còn nhiều mặt trái về gym vấn luôn tồn tại với những dục vọng cá nhân, long tham tiền bạc, hạnh phúc gia đình, thử thách chàng PT chân chính vượt qua.', '2023-01-15', 'https://i.ytimg.com/vi/BXB39-jD3z4/maxresdefault.jpg', 'https://www.youtube.com/embed/BXB39-jD3z4?si=32re_rqI3RaN446h', 1, 1, 2, 3),
    ('Gái Gymer yêu nhầm Trai Giang Hồ', 'Thu là 1 cô gái gymer yêu nhầm Huy (Duy Nguyễn đóng) là tay giang hồ khét tiếng quận 6, kinh hoàng với những phi vụ đẩm máu tranh giành địa bàn giữa các thế lực xã hội đen. Phim chứa nhiều từ ngữ dung tục và bạo lực không thích hợp lứa tuổi vị thành niên.', '2023-02-20', 'https://i.ytimg.com/vi/UGzVu0bVCRs/maxresdefault.jpg', 'https://www.youtube.com/embed/UGzVu0bVCRs?si=XEqgLPDr8lOo_oN9', 1, 2, 1, 4),
    ('Đấu La Đại Lục', 'Đệ tử ngoại môn Đường Tam của Đường Môn, vì học trộm bí kíp của nội môn mà bị Đường Môn bài xích, Đường Tam nhảy xuống vực để tỏ rõ ý chí nhưng không chết mà còn được bước vào một thế giới khác với một thân khác, một thế giới thuộc võ về được - là gọi Đại đấu La Lục. Nơi này không có ma pháp, không có đấu khí, không có võ thuật, nhưng lại có võ hồn kỳ thần. Ở nơi này, vào lúc 6 tuổi, đều sẽ ở trong điện võ hồn lệnh võ hồn thức tỉnh. Võ hồn có động vật, có thực vật, có thể đồ vật, chúng có phụ trợ mọi người sinh hoạt hằng ngày. Đó là một vài võ hồn biệt biệt xuất sắc có thể dùng để tu luyện đồng thời có thể tiến hành chiến đấu, nghiệp chức này trên Đấu La Đại là chức vụ Lục nhất nghiệp mạnh mẽ, cũng là vinh quang nhất —— Linh sư.', '2023-03-25', 'https://play-lh.googleusercontent.com/yvMsoDiXA6jkUFsW5HLQi54rhkufgMMJte4i2o7D7FuznzLIsmyEzUsweZgDfipzMg', 'https://www.youtube.com/embed/324vfLaJr60?si=IRDiBFFB83wCMt97', 1, 3, 3, 2),
    ('Đấu phá thương khung', 'Nội dung phim 4', '2023-04-10', 'data:image/jpeg;base64,', 'https://www.youtube.com/embed/3P0e4d18CzU?si=vaiykzzgh8DbU1Wr', 1, 4, 4, 5),
    ('Đấu phá thương khung', 'Đấu Phá Thương Khung kể về Tiêu Viêm là một võ giả trẻ có năng khiếu dị bẩm. Năm 9 tuổi, mẹ cậu bị truy sát đến chết, cha cậu giấu bặt chuyện này, Tiêu Viêm gìn giữ chiếc nhẫn mẹ để lại như báu vật không bao giờ rời. Công lực của Tiêu Viêm mãi đến năm 15 tuổi vẫn chưa có tiến triển, gia tộc có hôn ước với cậu từ lâu cũng đến để thoái hôn, khiến Tiêu gia phải chịu nỗi nhục to lớn. Tiêu Viêm một lần tình cờ đánh thức được chủ nhân chiếc nhẫn là Dược Trần lão nhân, dưới sự giúp đỡ của ông, võ công của Tiêu Viêm tiến bộ thần tốc, đồng thời biết được chủ mưu giết hại mẹ mình.<br>
Tiêu Viêm vào học viện Gia Nam học kỹ nghệ, tại đây quen biết được rất nhiều thầy tốt bạn hiền, lại một lần nữa thu hút sự chú ý của kẻ thù. Trong một lần tu luyện, Tiêu Viêm bị hại, sau khi thoát được cái chết, phát hiện gia tộc đã gặp tai họa ngập đầu. Vì muốn trả mối thù giết mẹ, đồng thời vì chính nghĩa giang hồ, Tiêu Viêm chọn cách một mình thách thức thế lực tà ác. Cuối cùng liên thủ với nữ hiệp Tiêu Huân Nhi, chung tay chiến thắng cường địch.', '2023-05-03', 'https://img.meta.com.vn/Data/image/2021/11/01/lich-chieu-dau-pha-khung-thuong-al.jpg', 'https://www.youtube.com/embed/3P0e4d18CzU?si=Amwn5ngmrTld7zMZ', 1, 1, 2, 1),
    ('Đặc vụ X', 'Bộ phim đi sâu vào thế giới của gián điệp, các mưu mô, và những trận đánh ác liệt. Những pha hành động đầy kịch tính, những cuộc đấu khẩu thông minh, và sự phát triển của nhân vật chính tạo nên một câu chuyện hấp dẫn và đầy thách thức.', '2023-07-19', 'https://i.ytimg.com/vi/mCnAvjTZCvg/hq720.jpg?sqp=-oaymwEhCK4FEIIDSFryq4qpAxMIARUAAAAAGAElAADIQj0AgKJD&rs=AOn4CLDsuiZf3roSpoxP1dK0ANxMFuHzYA', 'https://www.youtube.com/embed/mCnAvjTZCvg?si=UJZoFR1Iag8-qO5i', 1, 3, 1, 2),
    ('Gái ế', 'Bộ phim xoay quanh nhân vật chính là một cô gái độc lập và thành đạt trong sự nghiệp, nhưng lại gặp khó khăn trong việc tìm kiếm tình yêu đích thực. Cô đã trải qua nhiều cuộc hẹn hò và mối quan hệ nhưng không thành công, từ đó trở thành "gái ế". Mặc dù cô gái này có nhiều phẩm chất tốt, thông minh, duyên dáng và hài hước, nhưng cô vẫn không thể tìm thấy người đàn ông phù hợp.', '2023-08-28', 'https://i.ytimg.com/vi/RK-BQq3TCeo/hq720.jpg?sqp=-oaymwEhCK4FEIIDSFryq4qpAxMIARUAAAAAGAElAADIQj0AgKJD&rs=AOn4CLDYFXOzNFW9fYegprzTWQ9yB1Rhpw', 'https://www.youtube.com/embed/RK-BQq3TCeo?si=BZBv3a5JuU5nkXFK', 1, 4, 3, 5),
    ('Thôn nghèo', 'Bộ phim xoay quanh câu chuyện về một ngôi làng nhỏ nằm trong một vùng quê nghèo, nơi mà cuộc sống của người dân phụ thuộc vào nông nghiệp và những công việc lao động vất vả. Những nhân vật chính trong phim là những người dân bình thường, từ những nông dân chăm chỉ, thợ thủ công đến những người phụ nữ chăm sóc gia đình.', '2023-09-05', 'https://i.ytimg.com/vi/8gUD4yl3V5s/hq720.jpg?sqp=-oaymwEhCK4FEIIDSFryq4qpAxMIARUAAAAAGAElAADIQj0AgKJD&rs=AOn4CLATlKywT6bR4Fm4_F3BjewqqC9Uow', 'https://www.youtube.com/embed/8gUD4yl3V5s?si=igrotaSMXRkARJKw', 1, 1, 4, 3);
